interface LightboxProps {
  isOpen: boolean;
  currentIndex: number;
  images: Array<{
    src: string;
    title: string;
    description: string;
    location?: string;
    client?: string;
    year?: string;
  }>;
  onClose: () => void;
  onNext: () => void;
  onPrev: () => void;
}

function Lightbox({ isOpen, currentIndex, images, onClose, onNext, onPrev }: LightboxProps) {
  if (!isOpen) return null;

  const currentImage = images[currentIndex];

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div
      className="fixed inset-0 z-[1001] bg-black/50 inline-flex flex-col justify-center items-center gap-4 p-4"
      onClick={handleBackdropClick}
    >
      <div className="w-[900px] max-w-[960px] rounded-2xl flex flex-col justify-start items-start overflow-hidden">
        {/* Work Image Section */}
        <div
          className="self-stretch h-[462px] p-2 flex flex-col justify-start items-end bg-cover bg-center"
          style={{ backgroundImage: `url('${currentImage.src}')` }}
        >
          {/* Close Button */}
          <button
            onClick={onClose}
            className="p-1 bg-white rounded-lg inline-flex justify-start items-center gap-2 hover:bg-gray-100 transition-colors"
            aria-label="Close lightbox"
          >
            <div className="w-6 h-6 flex items-center justify-center">
              <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M10.5 3.5L3.5 10.5M3.5 3.5L10.5 10.5" stroke="black" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
          </button>
        </div>

        {/* Project Info Section */}
        <div className="self-stretch p-4 bg-black/60 inline-flex justify-start items-start gap-4">
          <div className="flex-1 max-w-96 inline-flex flex-col justify-start items-start gap-4">
            <div className="self-stretch justify-center text-white text-2xl font-medium font-['Rethink_Sans'] tracking-tight">
              {currentImage.title}
            </div>
            <div className="self-stretch flex flex-col justify-start items-start gap-1">
              {currentImage.location && (
                <div className="self-stretch inline-flex justify-start items-start gap-1">
                  <div className="justify-center text-white/50 text-base font-medium font-['Instrument_Sans']">Location:</div>
                  <div className="flex-1 justify-center text-white text-base font-medium font-['Instrument_Sans']">{currentImage.location}</div>
                </div>
              )}
              {currentImage.client && (
                <div className="self-stretch inline-flex justify-start items-start gap-1">
                  <div className="justify-center text-white/50 text-base font-medium font-['Instrument_Sans']">Client:</div>
                  <div className="flex-1 justify-center text-white text-base font-medium font-['Instrument_Sans']">{currentImage.client}</div>
                </div>
              )}
              {currentImage.year && (
                <div className="self-stretch inline-flex justify-start items-start gap-1">
                  <div className="justify-center text-white/50 text-base font-medium font-['Instrument_Sans']">Project Year:</div>
                  <div className="flex-1 justify-center text-white text-base font-medium font-['Instrument_Sans']">{currentImage.year}</div>
                </div>
              )}
            </div>
          </div>

          {/* Description Section */}
          <div className="flex-1 self-stretch px-2 pb-2 inline-flex flex-col justify-start items-start gap-7">
            <div className="self-stretch justify-center text-white text-base font-normal font-['Instrument_Sans']">
              {currentImage.description}
            </div>
          </div>
        </div>
      </div>

      {/* Pagination Container */}
      <div className="inline-flex justify-start items-center gap-5">
        {/* Previous Button */}
        <button
          onClick={onPrev}
          className="p-2 bg-white rounded-lg flex justify-center items-center disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100 transition-colors"
          disabled={currentIndex === 0}
        >
          <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M10 12L6 8L10 4" stroke="black" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>

        {/* Dots Container */}
        <div className="flex justify-start items-center">
          {images.map((_, index) => (
            <div key={index} className="w-6 h-6 flex items-center justify-center">
              <div className={`w-2 h-2 rounded-full transition-colors ${
                index === currentIndex ? 'bg-white' : 'bg-white/50'
              }`} />
            </div>
          ))}
        </div>

        {/* Next Button */}
        <button
          onClick={onNext}
          className="p-2 bg-white rounded-lg flex justify-center items-center disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-100 transition-colors"
          disabled={currentIndex === images.length - 1}
        >
          <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M6 4L10 8L6 12" stroke="black" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>
      </div>
    </div>
  );
}

function WorkImage({
  src,
  title,
  description,
  onClick,
  className = "",
  location,
  client,
  year
}: {
  src: string;
  title: string;
  description: string;
  onClick: () => void;
  className?: string;
  location?: string;
  client?: string;
  year?: string;
}) {
  return (
    <div
      className={`WorkImage ${className} cursor-pointer group relative overflow-hidden`}
      onClick={onClick}
    >
      <img
        data-layer="Work Image"
        data-hover="off"
        className="w-full h-full object-cover transition-all duration-300"
        src={src}
        alt={title}
        onMouseEnter={(e) => {
          e.currentTarget.setAttribute('data-hover', 'on');
        }}
        onMouseLeave={(e) => {
          e.currentTarget.setAttribute('data-hover', 'off');
        }}
      />

      {/* Project Info Overlay */}
      <div className="absolute inset-0 bg-black/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex flex-col justify-end">
        <div data-layer="Project Info" className="ProjectInfo self-stretch p-4 bg-black/60 inline-flex justify-start items-start gap-4">
          <div data-layer="Project Details" className="ProjectDetails flex-1 inline-flex flex-col justify-start items-start gap-4">
            <div data-layer="Project Title" className="ProjectTitle self-stretch justify-center text-white text-2xl font-medium font-['Rethink_Sans'] tracking-tight">{title}</div>
            <div data-layer="Project Metadata" className="ProjectMetadata self-stretch flex flex-col justify-start items-start gap-1">
              {location && (
                <div data-layer="Location Info" className="LocationInfo self-stretch inline-flex justify-start items-start gap-1">
                  <div data-layer="Location Label" className="LocationLabel justify-center text-white/50 text-base font-medium font-['Instrument_Sans']">Location:</div>
                  <div data-layer="Location Value" className="LocationValue flex-1 justify-center text-white text-base font-medium font-['Instrument_Sans']">{location}</div>
                </div>
              )}
              {client && (
                <div data-layer="Client Info" className="ClientInfo self-stretch inline-flex justify-start items-start gap-1">
                  <div data-layer="Client Label" className="ClientLabel justify-center text-white/50 text-base font-medium font-['Instrument_Sans']">Client:</div>
                  <div data-layer="Client Value" className="ClientValue flex-1 justify-center text-white text-base font-medium font-['Instrument_Sans']">{client}</div>
                </div>
              )}
            </div>
          </div>
          <div data-layer="Year Container" className="YearContainer w-10 self-stretch inline-flex flex-col justify-between items-center">
            <div data-layer="Arrow Icon" className="ArrowIcon w-12 h-12 relative overflow-hidden">
              <svg className="w-7 h-7 absolute left-[10.90px] top-[10.88px] text-white" fill="currentColor" viewBox="0 0 24 24">
                <path d="M7 17L17 7M17 7H7M17 7V17" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" fill="none"/>
              </svg>
            </div>
            {year && (
              <div data-layer="Year" className="Year self-stretch justify-center text-white/50 text-base font-medium font-['Instrument_Sans']">{year}</div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

