interface LightboxProps {
  isOpen: boolean;
  currentIndex: number;
  images: Array<{
    src: string;
    title: string;
    description: string;
    location?: string;
    client?: string;
    year?: string;
  }>;
  onClose: () => void;
  onNext: () => void;
  onPrev: () => void;
}

function Lightbox({ isOpen, currentIndex, images, onClose, onNext, onPrev }: LightboxProps) {
  if (!isOpen) return null;

  const currentImage = images[currentIndex];

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div
      className="fixed inset-0 z-[1001] bg-black/50 inline-flex flex-col justify-center items-center gap-2 md:gap-4 p-2 md:p-4"
      onClick={handleBackdropClick}
    >
      <div className="w-full max-w-[340px] sm:max-w-[500px] md:w-[900px] md:max-w-[960px] rounded-xl md:rounded-2xl flex flex-col justify-start items-start overflow-hidden">
        {/* Work Image Section */}
        <div
          className="self-stretch h-[240px] sm:h-[300px] md:h-[462px] p-1 md:p-2 flex flex-col justify-start items-end bg-cover bg-center"
          style={{ backgroundImage: `url('${currentImage.src}')` }}
        >
          {/* Close Button */}
          <button
            onClick={onClose}
            className="p-1 bg-white rounded-lg inline-flex justify-start items-center gap-2 hover:bg-gray-100 transition-colors"
            aria-label="Close lightbox"
          >
            <div className="w-6 h-6 flex items-center justify-center">
              <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M10.5 3.5L3.5 10.5M3.5 3.5L10.5 10.5" stroke="black" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
